import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ResourceTable = () => {
  const [resources, setResources] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [instanceId, setInstanceId] = useState(""); // Optionally set instanceId dynamically

  useEffect(() => {
    let apiUrl;

    if (instanceId) {
      // If instance ID is provided, fetch that specific resource
      apiUrl = `http://localhost:8080/api/resources/${instanceId}`;
    } else {
      // If no instance ID is provided, fetch all resources
      apiUrl = "http://localhost:8080/api/resources";
    }

    console.log("API URL: ", apiUrl); // Log the URL to check

    // Fetch data from the backend
    axios.get(apiUrl)
      .then(response => {
        setResources(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Failed to fetch resources');
        setLoading(false);
      });
  }, [instanceId]); // Re-run when instanceId changes

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      {/* Optionally, you can add an input to search for a specific instance */}
      <input 
        type="text"
        placeholder="Enter instance ID"
        value={instanceId}
        onChange={e => setInstanceId(e.target.value)} 
      />

      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Region</th>
            <th>Type</th>
            <th>Status</th>
            <th>IP</th>
            <th>Created Time</th>
          </tr>
        </thead>
        <tbody>
          {resources.map(resource => (
            <tr key={resource.name}>
              <td>{resource.name}</td>
              <td>{resource.zone}</td>
              <td>{resource.type}</td>
              <td>{resource.status || 'N/A'}</td>
              <td>{resource.ipAddresses && resource.ipAddresses.length > 0 ? resource.ipAddresses.join(', ') : 'N/A'}</td>
              <td>{resource.creationTimestamp || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ResourceTable;
